@bot.command(name='setface')
async def change_face(ctx, face_name: str):
    global current_face
    
    # Check if the face exists in the dictionary
    if face_name in faces:
        current_face = face_name
        await ctx.send(f"```Face changed to {face_name}!```")
    else:
        await ctx.send(f"```Face {face_name} not found, Available faces: {', '.join(faces.keys())}```")


